import React, { useState, useRef } from 'react';
import axios from 'axios';

const VisualMemorySearch = () => {
  const [screenshots, setScreenshots] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [isUploading, setIsUploading] = useState(false);
  const [isSearching, setIsSearching] = useState(false);
  const [uploadMessage, setUploadMessage] = useState('');
  const [error, setError] = useState('');
  const [imagePreviews, setImagePreviews] = useState({});
  const [uploadedFiles, setUploadedFiles] = useState({}); // Store actual file objects
  const [selectedImage, setSelectedImage] = useState(null); // For modal preview
  const fileInputRef = useRef(null);

  const handleFileUpload = async (event) => {
    const files = Array.from(event.target.files);
    if (files.length === 0) return;

    setIsUploading(true);
    setUploadMessage('');

    try {
      // Generate previews and store files for all images
      files.forEach(file => {
        if (file.type.startsWith('image/')) {
          generateImagePreview(file);
          // Store the actual file object for later use
          setUploadedFiles(prev => ({
            ...prev,
            [file.name]: file
          }));
        }
      });

      const formData = new FormData();
      files.forEach(file => {
        if (file.type.startsWith('image/')) {
          formData.append('files', file);
        }
      });

      const token = localStorage.getItem('token');
      const response = await axios.post('/visual-memory/upload-screenshots', formData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'multipart/form-data',
        },
      });

      setUploadMessage(`Successfully processed ${response.data.results.length} screenshots`);
      setScreenshots(prev => [...prev, ...response.data.results]);

      // Clear file input
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    } catch (error) {
      setUploadMessage(`Error: ${error.response?.data?.detail || error.message}`);
    } finally {
      setIsUploading(false);
    }
  };

  const handleSearch = async () => {
    if (!searchQuery.trim()) return;

    setIsSearching(true);
    setError('');
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('/visual-memory/search', 
        { text: searchQuery, max_results: 5 },
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );

      setSearchResults(response.data.results);
      if (response.data.results.length === 0) {
        setError('No screenshots found matching your query. Try different keywords.');
      }
    } catch (error) {
      console.error('Search error:', error);
      setError(error.response?.data?.detail || 'Search failed. Please try again.');
      setSearchResults([]);
    } finally {
      setIsSearching(false);
    }
  };

  const loadScreenshots = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('/visual-memory/screenshots', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
      setScreenshots(response.data.screenshots);

      // Note: For screenshots loaded from server, we won't have the original files
      // So we'll show placeholder previews. In a production app, you might want to
      // store the actual image files or implement a way to retrieve them.
    } catch (error) {
      console.error('Failed to load screenshots:', error);
    }
  };

  const generateImagePreview = (file) => {
    const reader = new FileReader();
    reader.onload = (e) => {
      setImagePreviews(prev => ({
        ...prev,
        [file.name]: e.target.result
      }));
    };
    reader.readAsDataURL(file);
  };

  return (
    <div className="max-w-6xl mx-auto p-6 space-y-8">
      <div className="text-center">
        <h1 className="text-4xl font-bold text-gray-900 mb-4">
          Visual Memory Search
        </h1>
        <p className="text-lg text-gray-600">
          Upload screenshots and search them using natural language queries
        </p>
      </div>

      {/* Upload Section */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-2xl font-semibold mb-4">Upload Screenshots</h2>
        <div className="space-y-4">
          <input
            ref={fileInputRef}
            type="file"
            multiple
            accept="image/*"
            onChange={handleFileUpload}
            className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
            disabled={isUploading}
          />
          {isUploading && (
            <div className="text-blue-600">Processing screenshots...</div>
          )}
          {uploadMessage && (
            <div className={`p-3 rounded ${uploadMessage.includes('Error') ? 'bg-red-100 text-red-700' : 'bg-green-100 text-green-700'}`}>
              {uploadMessage}
            </div>
          )}
        </div>
      </div>

      {/* Search Section */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-2xl font-semibold mb-4">Search Screenshots</h2>
        <div className="flex space-x-4">
          <input
            type="text"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            placeholder="e.g., 'error message about auth' or 'screenshot with blue button'"
            className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
          />
          <button
            onClick={handleSearch}
            disabled={isSearching || !searchQuery.trim()}
            className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {isSearching ? 'Searching...' : 'Search'}
          </button>
        </div>
        {error && (
          <div className="mt-4 p-3 bg-red-100 text-red-700 rounded-lg">
            {error}
          </div>
        )}
      </div>

      {/* Search Results */}
      {searchResults.length > 0 && (
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-2xl font-semibold mb-4">Search Results</h2>
          <div className="space-y-4">
            {searchResults.map((result, index) => (
              <div key={index} className="border border-gray-200 rounded-lg p-4">
                <div className="flex space-x-4">
                  {imagePreviews[result.filename] && (
                    <div className="flex-shrink-0">
                      <img 
                        src={imagePreviews[result.filename]} 
                        alt={result.filename}
                        className="w-32 h-32 object-cover rounded-lg border border-gray-200 shadow-sm"
                      />
                    </div>
                  )}
                  <div className="flex-1">
                    <div className="flex justify-between items-start mb-2">
                      <h3 className="font-semibold text-lg">{result.filename}</h3>
                      <span className="bg-blue-100 text-blue-800 px-2 py-1 rounded text-sm">
                        {Math.round(result.confidence_score * 100)}% match
                      </span>
                    </div>
                    <div className="text-sm text-gray-600 mb-2">
                      <strong>Text Content:</strong> {result.text_content.substring(0, 200)}...
                    </div>
                    <div className="text-sm text-gray-600 mb-2">
                      <strong>Visual Description:</strong> {result.visual_description}
                    </div>
                    <div className="text-xs text-gray-500">
                      Uploaded: {new Date(result.upload_time).toLocaleString()}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Screenshots List */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-2xl font-semibold">Your Screenshots</h2>
          <button
            onClick={loadScreenshots}
            className="px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700"
          >
            Refresh
          </button>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {screenshots.length === 0 ? (
            <p className="text-gray-500 col-span-full">No screenshots uploaded yet.</p>
          ) : (
            screenshots.map((screenshot, index) => (
              <div key={index} className="border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow">
                {/* Image Preview */}
                <div className="mb-3">
                  {imagePreviews[screenshot.filename] ? (
                    <img 
                      src={imagePreviews[screenshot.filename]} 
                      alt={screenshot.filename}
                      className="w-full h-48 object-cover rounded-lg border border-gray-200"
                    />
                  ) : (
                    <div className="w-full h-48 bg-gray-100 rounded-lg border border-gray-200 flex items-center justify-center">
                      <span className="text-gray-400">No preview available</span>
                    </div>
                  )}
                </div>

                {/* Screenshot Info */}
                <div className="space-y-2">
                  <h3 className="font-semibold text-sm truncate" title={screenshot.filename}>
                    {screenshot.filename}
                  </h3>
                  <div className="text-xs text-gray-500">
                    Uploaded: {new Date(screenshot.upload_time).toLocaleString()}
                  </div>
                  {screenshot.text_content && (
                    <div className="text-xs text-gray-600 line-clamp-2">
                      {screenshot.text_content.substring(0, 100)}...
                    </div>
                  )}
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default VisualMemorySearch;
